import { Pipe, PipeTransform } from '@angular/core'

@Pipe({
	name: 'classification',
	standalone: true
})
export class ClassificationPipe implements PipeTransform {
	transform(classification: string): string {
		return ` | ${classification}`
	}
}

import { Pipe, PipeTransform } from '@angular/core'

@Pipe({
	name: 'genre',
	standalone: true
})
export class GenrePipe implements PipeTransform {
	transform(genre: string[]): string {
		return genre.join(' / ')
	}
}

import { Pipe, PipeTransform } from '@angular/core'
import { formatDate } from '@angular/common'

@Pipe({
	name: 'runtime',
	standalone: true
})
export class RuntimePipe implements PipeTransform {
	transform(runtime: number): string {
		return runtime > 0 ? formatDate(runtime * 1000 * 60, "h 'hr' m 'min'", 'en-us', 'z') || '' : 'N/A'
	}
}

