import { HttpClient } from '@angular/common/http'
import { Injectable } from '@angular/core'
import { environment } from 'environments/environment'
import { BehaviorSubject, map, tap } from 'rxjs'
import { ConfigurationSchema } from '@schemas'

@Injectable({
	providedIn: 'root'
})
export class ApiConfigurationService {
	private apiUrl = environment.apiUrl

	private imagesPath$ = new BehaviorSubject<string>('')
	private posterSizes$ = new BehaviorSubject<string[]>([])
	private backdropSizes$ = new BehaviorSubject<string[]>([])

	constructor(private http: HttpClient) {}

	/**
	 * Fetches the API configuration details.
	 * @returns {Observable<boolean>} An Observable that emits a boolean value.
	 */
	getApiDetails() {
		return this.http.get<ConfigurationSchema>(`${this.apiUrl}/configuration`).pipe(
			map(result => result.images),
			tap(configuration => {
				this.imagesPath$.next(configuration.secure_base_url)
				this.posterSizes$.next(configuration.poster_sizes)
				this.backdropSizes$.next(configuration.backdrop_sizes)
			}),
			map(() => true)
		)
	}

	/**
	 * Returns the base URL for images.
	 * @returns {Observable<string>} An Observable that emits a string value.
	 */
	getImagesPath() {
		return this.imagesPath$.asObservable()
	}

	/**
	 * Returns the list of poster sizes.
	 * @returns {Observable<string[]>} An Observable that emits an array of strings.
	 */
	getPosterSizes() {
		return this.posterSizes$.asObservable()
	}

	/**
	 * Returns the list of backdrop sizes.
	 * @returns {Observable<string[]>} An Observable that emits an array of strings.
	 */
	getBackdropSizes() {
		return this.backdropSizes$.asObservable()
	}

	/**
	 * Determines if the API configuration has been loaded.
	 * @returns {boolean} Returns `true` if the API configuration has been loaded, `false` otherwise.
	 */
	isApiConfigurationLoaded() {
		return this.imagesPath$.value !== '' && this.posterSizes$.value.length > 0 && this.backdropSizes$.value.length > 0
	}
}

import { inject, Injectable } from '@angular/core'
import { of } from 'rxjs'
import { v4 as uuid } from 'uuid'
import { User } from '@models'
import { UserService } from '../user/user.service'

@Injectable({
	providedIn: 'root'
})
export class AuthService {
	userService = inject(UserService)

	/**
	 * Signs up a new user.
	 *
	 * @param user - The user object containing the user details.
	 * @returns {Observable<void>} An Observable that emits the result of the sign up operation.
	 */
	signUp(user: User) {
		const newUser: User = { ...user, id: uuid() }
		sessionStorage.setItem('user', JSON.stringify(newUser))
		return of(this.userService.setUser(newUser))
	}

	/**
	 * Signs out the user by removing the user session from the session storage.
	 * @returns {Observable<void>} An Observable that completes after removing the user session.
	 */
	signOut() {
		sessionStorage.removeItem('user')
		return of(this.userService.setUser(null))
	}

	/**
	 * Checks if the user is authenticated.
	 *
	 * This function checks if there is a 'user' item in the session storage.
	 * If the 'user' item exists, it means the user is authenticated.
	 *
	 * @returns {Observable<boolean>} An Observable that emits `true` if the user is authenticated, `false` otherwise.
	 */
	isAuthenticated() {
		return of(sessionStorage.getItem('user') !== null)
	}
}

import { HttpClient } from '@angular/common/http'
import { Injectable } from '@angular/core'
import { environment } from 'environments/environment'
import { BehaviorSubject, map, tap } from 'rxjs'
import { v4 as uuid } from 'uuid'
import { Classification } from '@models'
import { CertificationSchema } from '@schemas'

@Injectable({
	providedIn: 'root'
})
export class CertificationsService {
	private apiUrl = environment.apiUrl
	private movieCertifications$ = new BehaviorSubject<Classification[]>([])

	constructor(private http: HttpClient) {}

	/**
	 * Retrieves the movie certifications from the API and stores them in a subject.
	 */
	loadMovieCertifications() {
		return this.http
			.get<{ certifications: { US: CertificationSchema[] } }>(`${this.apiUrl}/certification/movie/list`)
			.pipe(
				map(({ certifications }) => {
					const classifications: Classification[] = certifications.US.map(certification => {
						return {
							id: uuid(),
							classification: certification.certification,
							meaning: certification.meaning
						}
					})

					return classifications
				}),
				tap(classifications => this.movieCertifications$.next(classifications)),
				map(() => true)
			)
	}

	/**
	 * Retrieves the movie certifications as an observable.
	 * @returns An observable that emits the movie certifications.
	 */
	getMovieCertifications() {
		return this.movieCertifications$.asObservable()
	}

	/**
	 * Checks if the movie certifications have been loaded.
	 * @returns A boolean indicating if the movie certifications have been loaded.
	 */
	areMovieCertificationsLoaded() {
		return this.movieCertifications$.value.length > 0
	}
}

