import { Component } from '@angular/core'
import { IMAGE_LOADER, ImageLoaderConfig } from '@angular/common'
import { RouterOutlet } from '@angular/router'
import { tuiIconResolverProvider, TuiRoot } from '@taiga-ui/core'

@Component({
	selector: 'app-root',
	standalone: true,
	imports: [RouterOutlet, TuiRoot],
	providers: [
		tuiIconResolverProvider(icon => {
			return icon.includes('tui') ? `assets/taiga-ui/icons/${icon.replace('@tui.', '')}.svg` : `icons/${icon}-duotone.svg`
		}),
		{
			provide: IMAGE_LOADER,
			useValue: (config: ImageLoaderConfig) => {
				const netlifyPath = 'https://cinemafun.netlify.app/.netlify/images'
				const imagePath = `${netlifyPath}?url=${config.src}`

				const queryParams = new URLSearchParams([])
				const paramMapping = new Map([
					['width', 'w'],
					['height', 'h'],
					['format', 'fm'],
					['quality', 'q'],
					['fit', 'fit'],
					['position', 'position']
				])

				paramMapping.forEach((value, key) => {
					if (config.loaderParams?.[key]) {
						queryParams.append(value, config.loaderParams[key])
					}
				})

				return `${imagePath}${queryParams.size > 0 ? `&${queryParams.toString()}` : ''}`
			}
		}
	],
	templateUrl: './app.component.html',
	styleUrl: './app.component.scss'
})
export class AppComponent {}

import { booleanAttribute, Component, input, model, output } from '@angular/core'
import { takeUntilDestroyed } from '@angular/core/rxjs-interop'
import { TuiButton, TuiSizeXL, TuiSizeXS } from '@taiga-ui/core'
import { TuiButtonLoading } from '@taiga-ui/kit'
import { combineLatest } from 'rxjs'
import { CoreService } from '@services'

@Component({
	selector: 'cf-button',
	standalone: true,
	imports: [TuiButton, TuiButtonLoading],
	templateUrl: './button.component.html',
	styleUrl: './button.component.scss'
})
export class ButtonComponent {
	type = input.required<'submit' | 'reset' | 'button'>()
	label = input('')
	form = input('')
	appearance = input<'primary' | 'accent'>('primary')
	iconLeading = input('')
	iconTrailing = input('')
	size = input<TuiSizeXS | TuiSizeXL>('l')
	isIconButton = input(false, { transform: booleanAttribute })
	isDisabled = model(false)
	isLoading = model(false)

	buttonClick = output()

	constructor(private coreService: CoreService) {
		combineLatest({
			_isLoading: this.coreService.getIsButtonLoading(),
			_isDisabled: this.coreService.getIsButtonDisabled()
		})
			.pipe(takeUntilDestroyed())
			.subscribe(({ _isLoading, _isDisabled }) => {
				this.isLoading.set(_isLoading)
				this.isDisabled.set(_isDisabled)
			})
	}

	onButtonClick() {
		this.buttonClick.emit()
	}
}

import { AfterViewInit, Component, ElementRef, input, model, Renderer2, viewChild } from '@angular/core'
import { AsyncPipe, NgClass, NgOptimizedImage, NgTemplateOutlet } from '@angular/common'
import { Router } from '@angular/router'
import { TuiHint, TuiSizeL, TuiSizeS } from '@taiga-ui/core'
import { TuiCarousel, TuiPagination } from '@taiga-ui/kit'
import { CarouselItem, LoaderParams } from '@models'
import { ApiConfigurationService } from '@services'
import { GenrePipe } from '@pipes'
import { ButtonComponent } from '../button/button.component'

@Component({
	selector: 'cf-carousel',
	standalone: true,
	imports: [
		AsyncPipe,
		NgClass,
		NgOptimizedImage,
		NgTemplateOutlet,
		TuiCarousel,
		TuiPagination,
		TuiHint,
		ButtonComponent,
		GenrePipe
	],
	templateUrl: './carousel.component.html',
	styleUrl: './carousel.component.scss'
})
export class CarouselComponent implements AfterViewInit {
	items = input.required<CarouselItem[]>()
	duration = input(10000)
	isDraggable = input(false)
	addFade = input(false)
	paginationSize = input<TuiSizeS | TuiSizeL>('s')
	loaderParams = input<LoaderParams>()
	index = model(0)

	pagination = viewChild<{ el: ElementRef<HTMLDivElement> }>('pagination')

	isImageLoading = true
	isSignUp = false

	imagesPath$ = this.apiConfigurationService.getImagesPath()
	backdropSizes$ = this.apiConfigurationService.getBackdropSizes()
	posterSizes$ = this.apiConfigurationService.getPosterSizes()

	constructor(
		private apiConfigurationService: ApiConfigurationService,
		private renderer: Renderer2,
		private router: Router
	) {
		this.isSignUp = this.router.url.includes('sign-up')
	}

	ngAfterViewInit(): void {
		const buttons = this.pagination()?.el.nativeElement.querySelectorAll('button')
		buttons?.forEach((button, index) => {
			this.renderer.setAttribute(button, 'aria-label', `carousel-button-${index + 1}`)
		})
	}

	/**
	 * Returns a media query string based on the provided size.
	 * @param {string} size - The size of the media query in the format 'Xw', where X is the width in pixels.
	 * @returns A media query string in the format '(max-width: Xpx)'.
	 */
	getMediaQuery(size: string) {
		const formattedSize = size.replace('w', '')
		return `(max-width: ${formattedSize}px)`
	}

	/**
	 * Callback function triggered when an image finishes loading.
	 * Sets the `isImageLoading` property to `false`.
	 */
	onImageLoad() {
		this.isImageLoading = false
	}

	/**
	 * Moves the carousel to the previous slide.
	 */
	previousSlide() {
		this.index.set(this.index() - 1 < 0 ? this.items().length - 1 : this.index() - 1)
	}

	/**
	 * Moves to the next slide in the carousel.
	 */
	nextSlide() {
		this.index.set(this.index() + 1 >= this.items().length ? 0 : this.index() + 1)
	}
}

