@let imagesPath = imagesPath$ | async;

<!-- Previous button -->
@if (!isSignUp) {
	<cf-button
		[isIconButton]="true"
		[type]="'button'"
		[iconLeading]="'tuiIconChevronLeftLarge'"
		[tuiHint]="'Previous'"
		(buttonClick)="previousSlide()"
		aria-label="Previous page" />
}

<!-- Carousel with pagination -->
<tui-carousel
	[duration]="duration()"
	[draggable]="isDraggable()"
	[(index)]="index"
	[ngClass]="{ fade: addFade(), rounded: !isSignUp }">
	@for (item of items(); track item.id) {
		<picture
			*tuiItem
			[tuiHint]="isSignUp ? item.hint : null"
			[tuiHintDescribe]="item.id"
			[tuiHintDirection]="'top-left'"
			[tuiHintShowDelay]="1000"
			tuiHintPointer
			[ngClass]="{ 'skeleton-image': isImageLoading }">
			@for (size of backdropSizes$ | async; track $index) {
				@let bannerPath = imagesPath + size + item.image;

				@if (size.includes('original')) {
					<img
						[ngSrc]="bannerPath"
						[loaderParams]="loaderParams()"
						[alt]="item.alt"
						(load)="onImageLoad()"
						priority
						placeholder
						fill />
				} @else {
					<source [srcset]="bannerPath" [media]="getMediaQuery(size)" />
				}
			}
			@if (!isSignUp) {
				<ng-container *ngTemplateOutlet="movieInfo; context: { movie: item }" />
			}
		</picture>
	}
